import java.util.*;
import java.io.*;

public class test {
    public static void main(String[] args) throws Exception {
        BufferedReader sc = new BufferedReader(new InputStreamReader(System.in));
        int[] arr = Arrays.stream(sc.readLine().split(" ")).mapToInt(Integer::parseInt).toArray();
        int[] queries = Arrays.stream(sc.readLine().split(" ")).mapToInt(Integer::parseInt).toArray();
        for (int x = 0; x < queries.length; x++) {
            int index = queries[x];
            int max = arr[index];
            int count = 0;
            for (int a = 0; a < index; a++)
                if (arr[a] < max)
                    count++;
            System.out.print(String.format("%2d: %2d, ", index, count));
        }
        System.out.println();
        // for (int x = 1; x < arr.length; x++)
        //     System.out.print(x + " ");
        for (int x = 0; x < arr.length; x++)
            System.out.print(x + ": " + arr[x] + ", ");
        sc.close();
    }
}
